import  test from '@playwright/test';
import expect from "@playwright/test";



test('filter form', async ({ page }) => {
    await page.goto('http://127.0.0.1:8000/add_filter');
  // going to the form page
    await expect(page).toHaveTitle(/add_filter/);
});

test('filling the filter form', async ({ page }) => {
    await page.goto('http://127.0.0.1:8000/add_filter');

    //Filling out the form
    await page.fill('DepartureDate', '2023-10-30');
    await page.fill('ReturnDate', '2023-11-11');
    await page.fill('City', 'New York');
    await page.fill('State', 'NY');
    await page.fill('Country', 'US');
    await page.fill('NumberOfTraveler', '2');
    await page.fill('BudgeMin', '2');
    await page.fill('BudgetMax', '100');


    // Click the 'start planning' button .
    await page.getByText('button').click();
    // Expects page to have a heading with the name of Installation.
    expect(page).toHaveTitle(/filter_results/);
});